--make the order of the users based on the date of they created 
select * from User_table
order by created_at   ;


-- What day of the week do most users register on?
SELECT
	DAYNAME(created_at) AS day_of_week,
	COUNT(id) AS num_users
FROM User_table
GROUP BY day_of_week
ORDER BY num_users DESC;


-- Target inactive users (no photos) with email campaign
SELECT
	username
FROM User_table
LEFT JOIN photos_table
	ON User_table.id = photos_table.user_id
GROUP BY username
HAVING COUNT(photos_table.id) = 0;

