	username
FROM User_table
LEFT JOIN photos_table
ON User_table.id = photos_table.user_id
WHERE photos_table.id IS NULL;


-- How many times does the average user posts on the instagram 
SELECT
(
	(SELECT COUNT(*) FROM photos_table)/
	(SELECT COUNT(*) FROM User_table)
) AS avg_posts;


-- how many times does a hash tag is used in the instagram
SELECT
	tag_name,
	COUNT(*) AS total
FROM tags
JOIN photo_tags
	ON tags.id = photo_tags.tag_id
GROUP BY tag_name
ORDER BY total DESC;

